import React, {Component} from "react";
import "./header_mob.css";
import logo from "../../PC/Header/img/logo.png";
import Sorts_mob from "../Sorts_mob/Sorts_mob";
import Docs_mob from "../Docs_mob/Docs_mob";
import Contacts_mob from "../Contacts_mob/Contacts_mob";

class Header_mob extends Component{
    constructor(props){
        super(props)

        this.state = {
            is_active: "",
            active_cmp: 0
        }

        this.cmps = [
            <Sorts_mob/>,
            <Docs_mob/>,
            <Contacts_mob/>
        ]

        this.changeActive = this.changeActive.bind(this)
    }

    changeActive(){
        if (this.state.is_active == "" || this.state.is_active == "unactive"){
            this.setState({
                is_active: "active"
            })
        }else{
            this.setState({
                is_active: "unactive"
            })
        }
    }

    render(){
        let menu_rects = Array(4).fill(0)
        return <div className="header_mob">
            <div className="main">
                <img src={logo}/>
                <button className="menu" onClick={this.changeActive}>
                    {menu_rects.map((item, index) => {
                        return <div key={index}></div>
                    })}
                </button>
            </div>
            <ul className={"left_menu "+this.state.is_active}>
                <li>Сорта</li>
                <li>Справочник</li>
                <li>Контакты</li>
            </ul>
        </div>
    }
}

export default Header_mob
