import React, {Component} from "react";
import "./sorts.css";
import SortCard from "./SortCard/SortCard";
import data from "../../data/main_sorts.json"
// import Fedor from 

class Sorts extends Component{
    constructor(props){
        super(props)

        this.state = {
            sorts: [],
            cur_card: null
        }

        this.setCurCard = this.setCurCard.bind(this)
    }

    componentDidMount(){
        // console.log(data)
        // data.map(item => {
        //     import image from `./img/${item.name}.jpg`
        //     item.image_path = image
        // })
        this.setState({
            sorts: data
        })
        // axios.get("http://localhost:"+this.props.app.port+"/api/get_main_sorts").then(response => {
        //     let sorts = response.data.result.map(item => {
        //         item.image_path = `data:image/jpeg;base64,${item.image_path}`
        //         return item
        //     })
        //     sorts = sorts.sort((a, b) => a.name > b.name ? 1 : -1);
        //     // console.log(sorts)
        //     this.setState(state => ({
        //         sorts: sorts
        //     }))
        // })
    }

    setCurCard(value){
        this.setState(state => ({
            cur_card: value
        }))
    }

    render(){
        return(
            <div className="sorts">
                <ul className="list">
                    {this.state.sorts.map((item, index) => {
                        return <li key={index}>
                            <div onClick={() => {this.setCurCard(index)}}>
                                <img src={item.image_path}/>
                                <h3 className="name">{item.name}</h3>
                            </div>
                        </li>
                    })}
                </ul>
                {this.state.cur_card==null?"":
                    <SortCard
                        data={this.state.sorts[this.state.cur_card]}
                        setCurCard={this.setCurCard}
                    />
                }
            </div>
        )
    }
}

export default Sorts
