import React, {Component} from "react";
import "./table.css";
import icon from "./img/icon.png";
import search from "./img/search.png";
import axios from "axios";
import { connect } from 'react-redux';

class Table extends Component{
    constructor(props){
        super(props)

        this.state = {
            is_closing: ""
        }

        this.closeTable = this.closeTable.bind(this)
    }

    componentDidMount(){
        axios.get(`http://localhost:${this.props.app.port}/api/get_table_info?db_name=${this.props.db_name}`).then(response => {
            console.log(response)
        })
        console.log(this.props.db_name)
    }

    closeTable(){
        this.setState(state => ({
            is_closing: "closing"
        }), () => {
            setTimeout(() => {this.props.openTable(null)}, 400)
        })
    }

    render(){
        return(
            <div className={"table "+this.state.is_closing}>
                <div className="main_wind">
                    <div className="top">
                        <img src={icon} className="icon"/>
                        <h3 className="title">Пшеница мягкая озимая</h3>
                        <div className="search">
                            <input
                                type="text"
                                placeholder="Название"
                            />
                            <button><img src={search}/></button>
                        </div>
                        <button className="close" onClick={this.closeTable}></button>
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state;
  }
  const mapDispatchToProps =  (dispatch) => {
    return {
      // "setTest": (data) => dispatch(setTest(data))
    }
  }
  export default connect(mapStateToProps, mapDispatchToProps)(Table)
