import React, {Component} from "react";
import "./sorts.css";
import axios from 'axios';
import { connect } from 'react-redux';
import SortCard from "./SortCard/SortCard";

class Sorts extends Component{
    constructor(props){
        super(props)

        this.state = {
            sorts: [],
            cur_card: null
        }

        setCurCard = this.setCurCard.bind(this)
    }

    componentDidMount(){
        axios.get("http://localhost:"+this.props.app.port+"/api/get_main_sorts").then(response => {
            let sorts = response.data.result.map(item => {
                item.image_path = `data:image/jpeg;base64,${item.image_path}`
                return item
            })
            this.setState(state => ({
                sorts: sorts
            }))
        })
    }

    setCurCard(value){
        this.setState(state => ({
            cur_card: value
        }))
    }

    render(){
        return(
            <div className="sorts">
                <ul className="list">
                    {this.state.sorts.map((item, index) => {
                        return <li key={index}>
                            <div>
                                <img src={item.image_path}/>
                                <h3 className="name">{item.name}</h3>
                            </div>
                        </li>
                    })}
                </ul>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
return state;
}
const mapDispatchToProps =  (dispatch) => {
return {
    // "setTest": (data) => dispatch(setTest(data))
}
}
export default connect(mapStateToProps, mapDispatchToProps)(Sorts)
