import React, {Component} from "react";
import "./sortCard.css";

class SortCard extends Component{
    constructor(props){
        super(props)

        this.state = {
            is_closing: ""
        }

        this.closeWindow = this.closeWindow.bind(this)
    }

    closeWindow(){
        this.setState(state => ({
            is_closing: "closing"
        }))
    }

    render(){
        return(
            <div className={"sortCard "+this.state.is_closing}>
                <div className="main_wind">
                    <div className="top">
                        <h3 className="name">{this.props.data.name}</h3>
                        <button className="close" onClick={this.closeWindow}></button>
                    </div>
                </div>
            </div>
        )
    }
}

export default SortCard
