import React, {Component} from "react";
import "./mob.css";

class Mob extends Component{
    constructor(props){
        super(props)
        this.state = {
            cur_back: 0
        }
    }

    changeBack(){
        setTimeout(() => {
            let cur_back = this.state.cur_back + 1
            if (cur_back == 3){cur_back = 0}
            this.setState(state => ({
                cur_back: cur_back
            }))
            this.changeBack()
        }, 10000)
    }

    componentDidMount(){
        this.changeBack()
    }

    render(){
        let back_arr = new Array(3).fill(0)
        return(
            <div className="mob">
                {back_arr.map((item, index) => {
                    return <div key={index}
                        className={"back "+"img_"+index+" "+((this.state.cur_back==index)?"active":"")}
                    />
                })}
            </div>
        )
    }
}

export default Mob
