import React, {Component} from "react";
import "./table.css";
import icon from "./img/icon.png";
import search from "./img/search.png";
import axios from "axios";
import { connect } from 'react-redux';

class Table extends Component{
    constructor(props){
        super(props)

        this.state = {
            is_closing: "",
            table_data: []
        }

        this.tables = [
            {
                header: ['Сорт', 'Год включения в реестр', 'Регион допуска', 'Качество по реестру', 'Качество по оригинатору'],
                keys: ['name', 'year', 'region', 'registry', 'originator']
            },
            [],
            [],
            [],
            [],
            [],
            [],
            [],
        ]

        this.closeTable = this.closeTable.bind(this)
    }

    componentDidMount(){
        axios.get(`http://localhost:${this.props.app.port}/api/get_table_info?db_name=${this.props.db_name}`).then(response => {
            console.log(response)
            this.setState(state => ({
                table_data: response.data.result
            }))
        })
    }

    closeTable(){
        this.setState(state => ({
            is_closing: "closing"
        }), () => {
            setTimeout(() => {this.props.openTable(null)}, 400)
        })
    }

    render(){
        return(
            <div className={"table "+this.state.is_closing}>
                <div className="main_wind">
                    <div className="top">
                        <img src={icon} className="icon"/>
                        <h3 className="title">Пшеница мягкая озимая</h3>
                        <div className="search">
                            <input
                                type="text"
                                placeholder="Название"
                            />
                            <button><img src={search}/></button>
                        </div>
                        <button className="close" onClick={this.closeTable}></button>
                    </div>
                    <div className="main_table">
                        {this.props.table_index==0?
                            <ul>
                                <li className="table_header">
                                    <div><p>№</p></div>
                                    {this.tables[this.props.table_index].header.map((item, index) => {
                                        return <div style={{flex: this.tables[this.props.table_index].flex[index]}}><p key={index}>{item}</p></div>
                                    })}
                                </li>
                            </ul>
                        :""}
                    </div>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return state;
  }
  const mapDispatchToProps =  (dispatch) => {
    return {
      // "setTest": (data) => dispatch(setTest(data))
    }
  }
  export default connect(mapStateToProps, mapDispatchToProps)(Table)
