import React, {Component} from "react";
import "./contacts.css";
import mapboxgl from "mapbox-gl";
import 'mapbox-gl/dist/mapbox-gl.css';
import home from "./img/home.png";
import phone from "./img/phone.png";
import email from "./img/email.png";

class Contacts extends Component{
    constructor(props){
        super(props)

        this.mapToken = "pk.eyJ1IjoiYmFsZWV2IiwiYSI6ImNsYXBqNmk4dTE5Y3UzcWxiYmt1bTJtcG8ifQ.aE8lRdfDnWq52szIP7gAHw"
        this.map = null

        this.order = {
            name: "",
            contact: "",
            order_text: ""
        }

        this.orderOnChange = this.orderOnChange.bind(this)
    }

    orderOnChange(e, key){

    }

    componentDidMount(){
        mapboxgl.accessToken = this.mapToken;
        this.map = new mapboxgl.Map({
            container: "map",
            style: 'mapbox://styles/mapbox/satellite-streets-v11',
            center: [38.670901, 47.276988],
            zoom: 16,
            minZoom: 2,
            attributionControl: false,
            interactive: false
        })
        let marker = new mapboxgl.Marker({
            "color": "#ff0000"
        }).setLngLat([38.670901, 47.276988]).addTo(this.map)
    }

    render(){
        return(
            <div className="contacts">
                 <div className="contacts_top">
                    <div id="map"></div>
                    <div  className="contacts_cont">
                        <h2>Свяжитесь с нами!</h2>
                        <ul>
                            <li>
                                <img src={home}/>
                                <span>Ростовская область, Неклиновский район, с. Носово, ул. Мира, 33</span>
                            </li>
                            <li>
                                <img src={phone}/>
                                <span><a className="link" href="tel:+7 (928) 607 30-60">+7 (928) 607 30-60</a></span>
                            </li>
                            <li>
                                <img src={email}/>
                                <span><a className="link" href="mailto:spk50let@mail.ru">spk50let@mail.ru</a></span>
                            </li>
                        </ul>
                    </div>
                 </div>
                 <div className="cont_callback">
                    <h2>Оставьте заявку</h2>
                    <input className="name"
                        type="text"
                        placeholder="Номер телефона или email"
                        value=""
                        onChange={(e) => {this.orderOnChange(e, "contact")}}
                    />
                    <input className="name"
                        type="text"
                        placeholder="Фамилия Имя Отчество"
                        value=""
                        onChange={(e) => {this.orderOnChange(e, "name")}}
                    />
                    <textarea className="order"
                        type="text"
                        placeholder="Что хотели бы приобрести"
                        value=""
                        onChange={(e) => {this.orderOnChange(e, "order_text")}}
                    />
                 </div>
            </div>
        )
    }
}

export default Contacts
